import { useState } from "react";
import ReactDOM from "react-dom";

function GetPolicyFormCard() {
  const [inputs, setInputs] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputs);
    document.getElementById("result").innerHTML = "Based on your input Policy HAPPINESS is best for you";
    //alert(inputs);
  }

  return (
    <form onSubmit={handleSubmit}>
    <label>Please provide us the information below ! <br></br><br></br>
     Based on this, FITINSURED Rule Engine will find and offer <br></br>a policy, that is best suited for you.</label>
      <br></br>
      <br></br>
      <br></br>
      <label>Enter your name : 
      <input 
        type="text" 
        name="username" 
        value=  {inputs.username || ""} 
        onChange={handleChange}
      />
      <br></br>
      <br></br>
      </label>
      <label>Contact Number : 
        <input 
          type="number" 
          name="age" 
          value=  {inputs.age || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>Do you smoke? :  
        <input 
          type="text" 
          name="smoke" 
          value=  {inputs.smoke || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>Do you exercise regularly? : 
        <input 
          type="text" 
          name="exercise" 
          value=  {inputs.exercise || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>Are you in good health? : 
        <input 
          type="text" 
          name="health" 
          value=  {inputs.health || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>...  More questions come here .... 
        </label>
        <br></br>
        <br></br>
        <label>Please upload your supporting documents here.
        </label>
        <br></br>
        <br></br>
        <div>
                <input type="file" />
                <br></br>
                <br></br>
                <button >
                  Upload the supporting documents here.
                </button>
                
        </div>
        <br></br>
        <br></br>
        <span><input type="submit"/> </span>
        <span></span>&nbsp;&nbsp;&nbsp;
        <span><button>Save, come back and submit later.</button></span>
        <br></br>
        <br></br>
       </form>
  )
}

export default GetPolicyFormCard;
