import { useState } from "react";
import ReactDOM from "react-dom";
import Footer from '../Footer';

function FitnessAppCard() {
  const [inputs, setInputs] = useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputs);
    document.getElementById("result").innerHTML = "Good job there! Come back tomorrow. <br></br>Your today's Rewards! <br></br> You can access some <a href src=#>healthy recipes here</a>.";
    //alert(inputs);
  }

  return (
    <>
    <form onSubmit={handleSubmit}>
    <label>Take a moment and log your fitness activities below!<br></br><br></br>
     40 days at a row will get you a reward.<br></br>
     <br></br>
     A better looking dashboard will earn you a discount.<br></br></label>
      <br></br>
      <br></br>
      <br></br>
      <label>Exercise Type : 
      <input 
        type="text" 
        name="username" 
        value=  {inputs.username || ""} 
        onChange={handleChange}
      />
      <br></br>
      <br></br>
      </label>
      <label>Exercise Duration : 
        <input 
          type="text" 
          name="age" 
          value=  {inputs.age || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>Steps Count if you did walking today :  
        <input 
          type="text" 
          name="smoke" 
          value=  {inputs.smoke || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>Calorie Intake: 
        <input 
          type="text" 
          name="exercise" 
          value=  {inputs.exercise || ""} 
          onChange={handleChange}
        />
        <br></br>
        <br></br>
        </label>
        <label>Fasting Hours: 
        <input 
          type="text" 
          name="exercise" 
          value=  {inputs.exercise || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <label>Water Intake: 
        <input 
          type="text" 
          name="exercise" 
          value=  {inputs.exercise || ""} 
          onChange={handleChange}
        />
        <br></br>
        <br></br>
        </label>
        <label>Today's weight: 
        <input 
          type="text" 
          name="exercise" 
          value=  {inputs.exercise || ""} 
          onChange={handleChange}
        />
        </label>
        <br></br>
        <br></br>
        <input type="submit" />
    </form>
    </>
  )
}

export default FitnessAppCard;
